/// Source of a file.
enum FileSource {
  /// Used if source is not specified.
  unspecified,

  /// Indicates the file is uploaded by the user.
  uploaded,

  /// Indicates the file is generated by Google.
  generated,

  /// Indicates the file is a registered file (e.g., Google Cloud Storage file).
  registered,
}

/// Converts a string to a [FileSource].
FileSource fileSourceFromString(String? value) {
  return switch (value?.toUpperCase()) {
    'UPLOADED' => FileSource.uploaded,
    'GENERATED' => FileSource.generated,
    'REGISTERED' => FileSource.registered,
    _ => FileSource.unspecified,
  };
}

/// Converts a [FileSource] to a string.
String fileSourceToString(FileSource source) {
  return switch (source) {
    FileSource.uploaded => 'UPLOADED',
    FileSource.generated => 'GENERATED',
    FileSource.registered => 'REGISTERED',
    FileSource.unspecified => 'SOURCE_UNSPECIFIED',
  };
}
